import React, { useState, useEffect } from 'react';
import './App.css';

function TaskCard({ id, header, content, updateTask, deleteTaskCard }) {
  const handleHeaderChange = (e) => {
    updateTask(id, 'header', e.target.value);
  };

  const handleContentChange = (e) => {
    updateTask(id, 'content', e.target.value);
  };

  const handleDelete = () => {
    deleteTaskCard(id);
  };

  return (
    <div className="taskcard">
      <input
        type="text"
        className="task-header"
        placeholder="Enter task header"
        value={header}
        onChange={handleHeaderChange}
      />
      <textarea
        className="task-content"
        placeholder="Enter your task here"
        value={content}
        onChange={handleContentChange}
      />
      <button className="delete-button" onClick={handleDelete}>
        Delete
      </button>
    </div>
  );
}

function App() {
  const [input, setInput] = useState('');
  const [todoList, setTodoList] = useState([]);
  const [progressList, setProgressList] = useState([]);
  const [completedList, setCompletedList] = useState([]);
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    const savedTasks = localStorage.getItem('tasks');
    if (savedTasks) {
      setTasks(JSON.parse(savedTasks));
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('tasks', JSON.stringify(tasks));
  }, [tasks]);

  const addTaskCard = () => {
    const newTaskCard = {
      id: Date.now(),
      header: '',
      content: '',
    };
    setTasks((prevTasks) => [...prevTasks, newTaskCard]);
  };

  const deleteTaskCard = (id) => {
    const updatedTasks = tasks.filter((taskCard) => taskCard.id !== id);
    setTasks(updatedTasks);

    setTodoList((prevList) => prevList.filter((task) => task.id !== id));
    setProgressList((prevList) => prevList.filter((task) => task.id !== id));
    setCompletedList((prevList) => prevList.filter((task) => task.id !== id));
  };

  const updateTask = (id, field, value) => {
    const updatedTasks = tasks.map((taskCard) => {
      if (taskCard.id === id) {
        return { ...taskCard, [field]: value };
      }
      return taskCard;
    });
    setTasks(updatedTasks);
    updateListContent(id, field, value);
  };

  const updateListContent = (id, field, value) => {
    setTodoList((prevList) => updateTaskContent(prevList, id, field, value));
    setProgressList((prevList) => updateTaskContent(prevList, id, field, value));
    setCompletedList((prevList) => updateTaskContent(prevList, id, field, value));
  };

  const updateTaskContent = (list, id, field, value) => {
    return list.map((task) => {
      if (task.id === id) {
        return { ...task, [field]: value };
      }
      return task;
    });
  };

  const handleClick = () => {
    const newTask = {
      id: Date.now(),
      task: input,
      complete: false,
    };
    setTodoList((prevList) => [...prevList, newTask]);
    setInput('');
    addTaskCard();
  };

  const handleMoveToProgress = (id) => {
    const task = todoList.find((task) => task.id === id);
    if (task) {
      setProgressList((prevList) => [
        ...prevList,
        { ...task, header: getTaskCardHeader(id), content: getTaskCardContent(id) },
      ]);
      setTodoList((prevList) => prevList.filter((task) => task.id !== id));
    }
  };

  const handleMoveToCompleted = (id) => {
    const task = progressList.find((task) => task.id === id);
    if (task) {
      setCompletedList((prevList) => [
        ...prevList,
        { ...task, header: getTaskCardHeader(id), content: getTaskCardContent(id) },
      ]);
      setProgressList((prevList) => prevList.filter((task) => task.id !== id));
    }
  };

  const getTaskCardHeader = (id) => {
    const taskCard = tasks.find((taskCard) => taskCard.id === id);
    return taskCard ? taskCard.header : '';
  };

  const getTaskCardContent = (id) => {
    const taskCard = tasks.find((taskCard) => taskCard.id === id);
    return taskCard ? taskCard.content : '';
  };

  return (
    <div className="App">
      <div className="lists-container">
        <div className="todo-list">
          <h2>Todo List</h2>
          <input value={input} onChange={(e) => setInput(e.target.value)} />
          <ul id="todoList">
            {todoList.map((todo) => {
              const taskCard = tasks.find((task) => task.id === todo.id);
              const header = taskCard ? taskCard.header : '';
              const content = taskCard ? taskCard.content : '';
              return (
                <li key={todo.id}>
                  {todo.task}
                  <div className="taskcard-container">
                    <TaskCard
                      id={todo.id}
                      header={header}
                      content={content}
                      updateTask={updateTask}
                      deleteTaskCard={deleteTaskCard}
                    />
                  </div>
                  <button onClick={() => handleMoveToProgress(todo.id)}>
                    Move to Progress
                  </button>
                </li>
              );
            })}
          </ul>
        </div>
        <div className="progress-list">
          <h2>Progress List</h2>
          <ul id="progress">
            {progressList.map((task) => {
              const taskCard = tasks.find((taskCard) => taskCard.id === task.id);
              const header = taskCard ? taskCard.header : '';
              const content = taskCard ? taskCard.content : '';
              return (
                <li key={task.id}>
                  {task.task}
                  <div className="taskcard-container">
                    <TaskCard
                      id={task.id}
                      header={header}
                      content={content}
                      updateTask={updateTask}
                      deleteTaskCard={deleteTaskCard}
                    />
                  </div>
                  <button onClick={() => handleMoveToCompleted(task.id)}>
                    Move to Completed
                  </button>
                </li>
              );
            })}
          </ul>
        </div>
        <div className="completed-list">
          <h2>Completed List</h2>
          <ul id="completed">
            {completedList.map((task) => {
              const taskCard = tasks.find((taskCard) => taskCard.id === task.id);
              const header = taskCard ? taskCard.header : '';
              const content = taskCard ? taskCard.content : '';
              return (
                <li key={task.id}>
                  {task.task}
                  <div className="taskcard-container">
                    <TaskCard
                      id={task.id}
                      header={header}
                      content={content}
                      updateTask={updateTask}
                      deleteTaskCard={deleteTaskCard}
                    />
                  </div>
                </li>
              );
            })}
          </ul>
        </div>
      </div>
      <button onClick={handleClick}>Add Task</button>
    </div>
  );
}

export default App;
