import React, { useState, useEffect } from 'react';
import './App.css';

function TaskCard({ taskId, header, content, updateTask, deleteTaskCard }) {
const handleHeaderChange = (e) => {
updateTask(taskId, 'header', e.target.value);
};

const handleContentChange = (e) => {
updateTask(taskId, 'content', e.target.value);
};

const handleDelete = () => {
deleteTaskCard(taskId);
};

return (
<div className="taskcard">
<input
     type="text"
     className="task-header"
     placeholder="Enter task header"
     value={header}
     onChange={handleHeaderChange}
   />
<textarea
     className="task-content"
     placeholder="Enter your task here"
     value={content}
     onChange={handleContentChange}
   />
<button className="delete-button" onClick={handleDelete}>
Delete
</button>
</div>
);
}

function App() {
const [input, setInput] = useState('');
const [todoList, setTodoList] = useState([]);
const [progressList, setProgressList] = useState([]);
const [completedList, setCompletedList] = useState([]);
const [tasks, setTasks] = useState([]);

useEffect(() => {
const savedTasks = localStorage.getItem('tasks');
if (savedTasks) {
setTasks(JSON.parse(savedTasks));
}
}, []);

useEffect(() => {
localStorage.setItem('tasks', JSON.stringify(tasks));
}, [tasks]);

const addTaskCard = () => {
const newTaskCard = {
id: Date.now(),
header: '',
content: '',
};
setTasks([...tasks, newTaskCard]);
};

const deleteTaskCard = (taskId) => {
const updatedTasks = tasks.filter((taskCard) => taskCard.id !== taskId);
setTasks(updatedTasks);
};

const updateTask = (taskId, field, value) => {
const updatedTasks = tasks.map((taskCard) => {
if (taskCard.id === taskId) {
return { ...taskCard, [field]: value };
}
return taskCard;
});
setTasks(updatedTasks);
};

const handleClick = () => {
const id = todoList.length + 1;
const newTask = {
id: id,
task: input,
complete: false,
};
setTodoList((prev) => [...prev, newTask]);
setInput('');
addTaskCard();
};

const handleMoveToProgress = (id) => {
const task = todoList.find((task) => task.id === id);
if (task) {
setProgressList((prev) => [...prev, task]);
setTodoList((prev) => prev.filter((task) => task.id !== id));
}
};

const handleMoveToCompleted = (id) => {
const task = progressList.find((task) => task.id === id);
if (task) {
setCompletedList((prev) => [...prev, task]);
setProgressList((prev) => prev.filter((task) => task.id !== id));
}
};

return (
<div className="App">
<div className="lists-container">
<div className="todo-list">
<h2>Todo List</h2>
<input value={input} onChange={(e) => setInput(e.target.value)} />
<ul id="todoList">
{todoList.map((todo) => (
<li key={todo.id}>
{tasks.map((taskCard) => (
<TaskCard
                 key={taskCard.id}
                 taskId={taskCard.id}
                 header={taskCard.header}
                 content={taskCard.content}
                 updateTask={updateTask}
                 deleteTaskCard={deleteTaskCard}
               />
))}
{todo.task}
<button onClick={() => handleMoveToProgress(todo.id)}>
Move to Progress
</button>
</li>
))}
</ul>
</div>
<div className="progress-list">
<h2>Progress List</h2>
<ul id="progress">
{progressList.map((task) => (
<li key={task.id}>
{tasks.map((taskCard) => (
<TaskCard
                 key={taskCard.id}
                 taskId={taskCard.id}
                 header={taskCard.header}
                 content={taskCard.content}
                 updateTask={updateTask}
                 deleteTaskCard={deleteTaskCard}
               />
))}
{task.task}
<button onClick={() => handleMoveToCompleted(task.id)}>
Move to Completed
</button>
</li>
))}
</ul>
</div>
<div className="completed-list">
<h2>Completed List</h2>
<ul id="completed">
{completedList.map((task) => (
<li key={task.id}>
{task.task}
<div className="taskcard-container">
{tasks.map((taskCard) => (
<TaskCard
                   key={taskCard.id}
                   taskId={taskCard.id}
                   header={taskCard.header}
                   content={taskCard.content}
                   updateTask={updateTask}
                   deleteTaskCard={deleteTaskCard}
                 />
))}
</div>
</li>
))}
</ul>
</div>
</div>
<button className="add-button" onClick={handleClick}>
Add Task
</button>
</div>
);
}

export default App;
